"use strict";

$(".accordeon__title").on("click", function () {
  $(this).closest(".accordeon__item").toggleClass("active"), $(this).parent().find(".accordeon__body").slideToggle(300);
}), $(".burger").on("click", function () {
  $(".header__desktop").slideDown(300), $("body").addClass("no-scroll");
}), $(".header__close").on("click", function () {
  $(".header__desktop").slideUp(300), $("body").removeClass("no-scroll");
});